import { useState, useCallback } from "react";
import "./App.css";

const WORD_LENGTH = 5;
const MAX_ATTEMPTS = 6;

const checkGuess = async (guess) => {
  const response = await fetch("http://localhost:5000/check", {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({ guess }),
    credentials: 'same-origin', // Ensure session cookies are sent with the request
  });
  return response.json();
};

const restartGame = async () => {
  await fetch("http://localhost:5000/restart", {
    method: "POST",
    credentials: 'same-origin', // Ensure session cookies are sent with the request
  });
};

export default function WordleGame() {
  const [guesses, setGuesses] = useState([]);
  const [currentGuess, setCurrentGuess] = useState("");
  const [feedback, setFeedback] = useState([]);
  const [gameOver, setGameOver] = useState(false);

  const handleInputChange = useCallback((e) => {
    if (e.target.value.length <= WORD_LENGTH) {
      setCurrentGuess(e.target.value.toUpperCase());
    }
  }, []);

  const handleSubmit = useCallback(async () => {
    if (currentGuess.length === WORD_LENGTH && guesses.length < MAX_ATTEMPTS) {
      const result = await checkGuess(currentGuess);
      setGuesses((prevGuesses) => [...prevGuesses, currentGuess]);
      setFeedback((prevFeedback) => [...prevFeedback, result.feedback]);
      setCurrentGuess("");

      if (result.gameOver) {
        setGameOver(true);
      }

      if (guesses.length + 1 === MAX_ATTEMPTS) {
        setGameOver(true); // Force game over after reaching max attempts
      }
    }
  }, [currentGuess, guesses]);

  const handleRestart = useCallback(async () => {
    await restartGame();
    setGuesses([]);
    setFeedback([]);
    setCurrentGuess("");
    setGameOver(false); // Reset game over state
  }, []);

  return (
    <div className="flex flex-col items-center p-4">
      <h1 className="text-2xl font-bold">Wordle Clone</h1>
      {guesses.map((guess, index) => (
        <div key={index} className="flex gap-2 my-2">
          {guess.split("").map((char, i) => (
            <span
              key={i}
              className={`w-10 h-10 flex items-center justify-center text-lg font-bold text-white rounded ${
                feedback[index][i] === "green"
                  ? "bg-green-500"
                  : feedback[index][i] === "yellow"
                  ? "bg-yellow-500"
                  : "bg-gray-500"
              }`}
            >
              {char}
            </span>
          ))}
        </div>
      ))}
      {!gameOver && guesses.length < MAX_ATTEMPTS && (
        <div className="flex gap-2 my-4">
          {/* Loop through each letter of currentGuess and render each in a box */}
          {Array.from({ length: WORD_LENGTH }).map((_, i) => (
            <span
              key={i}
              className="w-10 h-10 flex items-center justify-center text-lg font-bold text-white border border-gray-400 rounded"
            >
              {currentGuess[i] || ""}
            </span>
          ))}
          <button
            onClick={handleSubmit}
            className="bg-blue-500 text-white px-4 py-2 rounded"
            disabled={currentGuess.length !== WORD_LENGTH}
          >
            Submit
          </button>
        </div>
      )}
      {gameOver && (
        <div className="text-center mt-4">
          <p className="text-green-600 font-bold">Game Over!</p>
          <button
            onClick={handleRestart}
            className="bg-red-500 text-white px-4 py-2 rounded mt-2"
          >
            Restart Game
          </button>
        </div>
      )}
    </div>
  );
}
